import { Component, OnInit } from '@angular/core';
import { Route, Router } from '@angular/router';
import { User } from 'src/app/model/user';
import { AuthService } from 'src/app/service/auth.service';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent implements OnInit {
 

    name : string = '';
    username : string = '';
    password : string = '';
  
    user:User=new User();
  
    constructor(private authService:AuthService,private route:Router) { }
  
    ngOnInit(): void {
      this.username='';
      this.password='';
      this.name='';
    }
    signup(){
      // console.log(this.name+" "+this.username+" "+this.password)
      this.user.username=this.username;
      this.user.password=this.password;
      this.user.name=this.name;
      this.user.role="user";
      this.authService.signUp(this.user).subscribe(res=>{
        if(res==null){
          alert("Registration failed");
          this.ngOnInit();
        }
        else{
          console.log("Registration successfull");
          alert("Registratioin Succesfull");
          this.route.navigate(['/']);
        }
      }, err => {
    alert("Registration failed");
    this.ngOnInit();
      })
    }
  

}
<div class="container-fluid">
    <div class="row">
        <div class="col-md-6">
            <img src="../../../assets/login_img.img" height="100%" width="100%">
        </div>
        <div class="col-md-4 mt-3 mb-2">
            <h3 class="text-success text-center"><b>Welcome </b></h3>

            <form action="" class="mt-5 p-5">
                <h5 class="text-primary mb-3">Sign up </h5>
                <div class="form-floating mb-3">
                    <input type="text" class="form-control" id="floatingInput" placeholder="Username" name="name" [(ngModel)]="name">
                    <label for="floatingInput">Full name</label>
                </div>
                <div class="form-floating mb-3">
                    <input type="text" class="form-control" id="floatingInput" placeholder="Username" name="username" [(ngModel)]="username">
                    <label for="floatingInput">Email address</label>
                </div>
                  <div class="form-floating mb-3">
                    <input type="password" class="form-control" id="floatingPassword" placeholder="Password" name="password" [(ngModel)]="password">
                    <label for="floatingPassword">Password</label>
                  </div>
                  
                  <div class="d-grid gap-2 d-md-flex justify-content-md-end">
                    <button class="btn btn-outline-success me-md-2" type="button" (click)="signup()" >Register</button>
                  </div>

                  <a routerLink="/" class="text-primary" style="text-decoration: none;">Log In ? </a>
            </form>
        </div>
        <div class="col-md-2">
        </div>
    </div>
</div>
